package gowri_flowcontrol_iterative;

public class giforEachloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//for each loop exclusively to loop through elements in an array
//syntax: for (type variableName : arrayName){
		//code to be executed}
		String[] cars = {"volvo","lexsus","hundai","maruthi"};
		for (String i : cars) {
		System.out.println(i);
		}
	}

}
