package gowriflowcontrol_selective;

public class gifelse_operaters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int age = 90;
        if (age < 25) {
	    System.out.println("age is greater than 50 so, old"); 
	    }
	    else if (age <= 50 && age >92) {
	    	System.out.println("middle age"); 
	    }
	    else if (age <=33 && age == 25) {
        	System.out.println("young age");   
        }
	    else if (age ==50 || age < 89) {
	    	System.out.println("old");
	    }
	    else {
	    	System.out.println("young");
	     	
	    }
        
        
        }
        

}
