package gowriflowcontrol_selective;

public class gifelsepractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//if to specify block of code to be executed, if condition is true
//else to specify block of code to be executed, if the same above if condition is false
		
		int x= 20;
		if(x < 18) {
		System.out.println("20 is less than 18");
	    }
		else 
		{
		System.out.println("20 is greater than 18");
	}

}
}
