package gowritrasfer_statements;

public class gtransfer_continue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//continue statement breaks one iteration(in the loop) if specified condition occur and 
//- continues with the next iteration in the loop.
	
		
		for (int i = 0; i < 10; i++) {
			if (i == 4) {
			 continue;
			 	}
			System.out.println(i);
			
		}
	}

}
